       implicit none
       integer nd, nd1a,nd1b,nd2a,nd2b,nd3a,nd3b, ndu1a,ndu1b,ndu2a,
     & ndu2b,ndu3a,ndu3b,ndu4a,ndu4b,ndd1a,ndd1b,ndd2a,ndd2b,ndd3a,
     & ndd3b,ndd4a,ndd4b

       real rsxy(nd1a:nd1b,nd2a:nd2b,nd3a:nd3b,1:nd,1:nd)
