                 do i1=n1a,n1b
                   deriv(i1,i2,i3,0)=uy83r(i1,i2,i3,2)-uz83r(i1,i2,i3,
     & 1)
                   deriv(i1,i2,i3,1)=uz83r(i1,i2,i3,0)-ux83r(i1,i2,i3,
     & 2)
                   deriv(i1,i2,i3,2)=ux83r(i1,i2,i3,1)-uy83r(i1,i2,i3,
     & 0)
                 end do
                 end do
                 end do
             end if
           else
             if( nd.eq.2 )then
! loops3nc(deriv(i1,i2,i3,0)=ux82(i1,i2,i3,1)-uy82(i1,i2,i3,0),,)
                 do i3=n3a,n3b
                 do i2=n2a,n2b
                 do i1=n1a,n1b
                   deriv(i1,i2,i3,0)=ux82(i1,i2,i3,1)-uy82(i1,i2,i3,0)


