                 do i1=n1a,n1b
                   deriv(i1,i2,i3,0)=uy83(i1,i2,i3,2)-uz83(i1,i2,i3,1)
                   deriv(i1,i2,i3,1)=uz83(i1,i2,i3,0)-ux83(i1,i2,i3,2)
                   deriv(i1,i2,i3,2)=ux83(i1,i2,i3,1)-uy83(i1,i2,i3,0)
                 end do
                 end do
                 end do
             end if
           endif
        end if



